 /*
 import React, { useState } from "react";
import axios from "axios";

function App() {
  const [customerId, setCustomerId] = useState("");
  const [recommendationText, setRecommendationText] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:5000/api/recommend", {
        customer_id: customerId,
      });
      setRecommendationText(response.data.recommendations_text);
    } catch (error) {
      console.error("Error fetching recommendations:", error);
    }
  };

  return (
    <div className="App">
      <h1>Smartcart AI Recommendations</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Enter Customer ID"
          value={customerId}
          onChange={(e) => setCustomerId(e.target.value)}
        />
        <button type="submit">Get Recommendations</button>
      </form>

      <div className="recommendation-output">
        {recommendationText && (
          <>
            <h2>ðŸŽ¯ Final Recommendations:</h2>
            <pre style={{ whiteSpace: "pre-wrap", backgroundColor: "#f5f5f5", padding: "1rem" }}>
              {recommendationText}
            </pre>
          </>
        )}
      </div>
    </div>
  );
}

export default App;
*/
