import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [customerId, setCustomerId] = useState('');
  const [recommendationText, setRecommendationText] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleRecommend = async () => {
    setLoading(true);
    setError('');
    try {
      const response = await axios.post('http://localhost:5000/api/recommend', {
        customer_id: customerId,
      });

      setRecommendationText(response.data.recommendations);
    } catch (err) {
      setError('Error getting recommendations');
    }
    setLoading(false);
  };

  return (
    <div className="App">
      <h1>SmartCart AI - Personalized Recommendations</h1>
      <input
        type="text"
        placeholder="Enter Customer ID"
        value={customerId}
        onChange={(e) => setCustomerId(e.target.value)}
      />
      <button onClick={handleRecommend}>Get Recommendations</button>

      {loading && <p>Loading...</p>}
      {error && <p style={{ color: 'red' }}>{error}</p>}

      {recommendationText && (
        <div className="recommendation-box">
          <h2>ðŸŽ¯ Final Recommendations:</h2>
          <pre style={{ textAlign: 'left', whiteSpace: 'pre-wrap' }}>{recommendationText}</pre>
        </div>
      )}
    </div>
  );
}

export default App;
