// frontend/src/App.js
import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [customerId, setCustomerId] = useState('');
  const [recommendations, setRecommendations] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const fetchRecommendations = async () => {
    if (!customerId.trim()) {
      setError('Please enter a Customer ID');
      return;
    }

    setLoading(true);
    setError('');
    setRecommendations([]);

    try {
      const response = await axios.post('http://127.0.0.1:5000/api/recommend', {
        customer_id: customerId,
      });

      setRecommendations(response.data.recommendations || []);
    } catch (err) {
      setError('Something went wrong. Please check the backend or customer ID.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 flex items-center justify-center p-4">
      <div className="bg-white shadow-lg rounded-2xl p-8 max-w-2xl w-full">
        <h1 className="text-3xl font-bold text-center text-blue-700 mb-6">
          üõí Smartcart AI Recommender
        </h1>

        <div className="flex flex-col sm:flex-row gap-4 mb-6">
          <input
            type="text"
            placeholder="Enter Customer ID (e.g., C1000)"
            className="border border-gray-300 rounded-md px-4 py-2 w-full"
            value={customerId}
            onChange={(e) => setCustomerId(e.target.value)}
          />
          <button
            onClick={fetchRecommendations}
            className="bg-blue-600 hover:bg-blue-700 text-white font-semibold px-6 py-2 rounded-md transition"
          >
            Get Recommendations
          </button>
        </div>

        {loading && (
          <div className="text-center text-gray-600">
            ‚è≥ Generating personalized recommendations...
          </div>
        )}

        {error && (
          <div className="text-red-500 text-center font-medium mb-4">
            ‚ùå {error}
          </div>
        )}

        {recommendations.length > 0 && (
          <div className="space-y-4">
            <h2 className="text-xl font-semibold text-green-700">üéØ Final Recommendations:</h2>
            {recommendations.map((rec, index) => (
              <div key={index} className="bg-green-50 border border-green-200 rounded-lg p-4">
                <p className="font-semibold">
                  {index + 1}. Product ID: <span className="text-blue-600">{rec.product_id}</span>
                </p>
                <p className="text-sm text-gray-700">
                  Category: <strong>{rec.category}</strong>, Subcategory: <strong>{rec.subcategory}</strong>, Price: ‚Çπ{rec.price || 'N/A'}, Brand: <strong>{rec.brand || 'N/A'}</strong>
                </p>
                <p className="mt-1 text-gray-600 italic">üí° {rec.reason}</p>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
