import React, { useState } from 'react';
import './App.css';

function App() {
  const [customerId, setCustomerId] = useState('');
  const [recommendations, setRecommendations] = useState([]);
  const [error, setError] = useState('');

  const fetchRecommendations = async () => {
    if (!customerId.trim()) {
      setError('Please enter a valid Customer ID.');
      return;
    }
    setError('');
    try {
      const response = await fetch('http://localhost:5000/api/recommend', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ customer_id: customerId }),
      });
      const data = await response.json();
      setRecommendations(data);
    } catch (err) {
      setError('Server error. Please try again later.');
    }
  };

  return (
    <div className="app">
      <h1>ðŸ›’ SmartCart AI</h1>
      <div className="input-section">
        <input
          type="text"
          value={customerId}
          placeholder="Enter Customer ID (e.g., C1000)"
          onChange={(e) => setCustomerId(e.target.value)}
        />
        <button onClick={fetchRecommendations}>Get Recommendations</button>
        {error && <p className="error">{error}</p>}
      </div>

      {recommendations.length > 0 && (
        <div className="recommendations">
          <h2>ðŸŽ¯ Recommended Products</h2>
          {recommendations.map((product, index) => (
            <div className="card" key={index}>
              <h3>{index + 1}. {product.Product_ID} - {product.Category} ({product.Subcategory})</h3>
              <ul>
                <li><strong>Brand:</strong> {product.Brand}</li>
                <li><strong>Price:</strong> â‚¹{product.Price}</li>
                <li><strong>Product Rating:</strong> {product.Product_Rating}</li>
                <li><strong>Sentiment Score:</strong> {product.Customer_Review_Sentiment_Score}</li>
                <li><strong>Season:</strong> {product.Season}</li>
                <li><strong>Geographical Location:</strong> {product.Geographical_Location}</li>
                <li><strong>Probability of Recommendation:</strong> {product.Probability_of_Recommendation}</li>
              </ul>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default App;
